//   const theme = useTheme();
//   const [mobileOpen, setMobileOpen] = React.useState(false);
//
//   const handleDrawerToggle = () => {
//     setMobileOpen(!mobileOpen);
//   };

/**
 * Simple component with no state.
 *
 * See the basic-react from lecture 11 for an example of adding and
 * reacting to changes in state and lecture 16 for details on Material-UI
 *
 * Using Material UI App Bar https://material-ui.com/components/app-bar/
 *
 * @return {object} JSX
 */
function App() {
  const classes = useStyles();

  return (
    <div>
      <div className='TestAppBar'>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6">
              CSE183 Mail - Inbox
            </Typography>
          </Toolbar>
        </AppBar>
      </div>
      <nav className={classes.drawer} aria-label="mailbox folders">
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <div>
        <h2>Let&apos;s make this look way better with Material-UI, eh?</h2>
        <table>
          <tbody>
            {emails.map((email) => (
              <tr key={email.id}>
                <td>{email.from}</td>
                <td>{email.subject}</td>
                <td>{email.received}</td>
                <td>{email.trash ? 'Trash' : 'Inbox'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
